import { Section } from './Section/Section';
import { FeedbackOptions } from './FeedbackOptions/FeedbackOptions'
import { Statistics } from './Statistics/Statistics'
import { Notification } from './Notification/Notification'

import css from "./App.module.css"
import { useState } from "react";



export const App2 = () => {

    const [good, setGood] = useState(0);
    const [neutral, setNeutral] = useState(0);
    const [bad, setBad] = useState(0);
    const [total, setTotal] = useState(0);
    const [positivePercentage, setPositivePercentage] = useState(0);



    // const countTotalFeedback = () => {
    //     setTotal(good + neutral + bad)
    //     // this.setState((state) => ({
    //     //     total: state.good + state.neutral + state.bad,
    //     // }))
    // }

    // const countPositiveFeedbackPercentage = () => {

    //     setPositivePercentage(Math.floor((good / total) * 100))

    // this.setState((state) => ({
    //     positivePercentage: Math.floor((state.good / state.total) * 100),
    // }))


    useEffect(() => {
        setPositivePercentage(Math.floor((good / total) * 100))

    }, [good, total]);



    const handleIncrement = (e) => {

        const { name } = e.target
        switch (name) {
            case 'good':
                setGood(good + 1);
                setTotal(total + 1);

                break;
            case 'neutral':
                setNeutral(neutral + 1);
                setTotal(total + 1);
                break;
            case 'bad':
                setBad(bad + 1);
                setTotal(total + 1);
                break;
            default:
                console.log("error")
        }

    }




    return (
        <div className={css.container}>
            <Section title="Please leave feedback">
                <FeedbackOptions onLeaveFeedback={handleIncrement}  ></FeedbackOptions>
            </Section >
            <Section title="Statistics">
                {total === 0
                    ? <Notification message="There is no feedback"></Notification>
                    : <Statistics good={good} neutral={neutral} bad={bad} total={total} positivePercentage={positivePercentage}></Statistics>}
            </Section>
        </div>
    )
};
